insert.selectKey:
SELECT mtNum
FROM mtLike
WHERE mtNum = #{mntilistno}
AND userId = #{userId}

mapper.select:

SELECT LIKEYN
FROM MTLIKE
WHERE mtNum = #{mntilistno}
  AND userId = #{userId}

SELECT *
FROM trailLocation
WHERE MNTN_CODE = #{MNTN_CODE}
AND FID = #{FID}
ORDER BY locationorder asc

SELECT ti.PMNTN_LT, ti.PMNTN_GODN, ti.MNTN_CODE, ti.PMNTN_MAIN, ti.PMNTN_UPPL, ti.PMNTN_RISK, ti.PMNTN_CLS_, ti.PMNTN_NM, ti.PMNTN_MTRQ, ti.DATA_STDR_, ti.PMNTN_DFFL, ti.MNTN_NM, ti.PMNTN_CNRL, ts.LOCATIONX, ts.LOCATIONY
FROM trailInfo ti, trailspot ts
WHERE ti.MNTN_CODE = ts.MNTN_CODE
  AND ts.FID = 0
  AND ti.MNTN_CODE = #{MNTN_CODE}
  AND ti.FID = #{FID}
