mapper.select:
SELECT count(*)
FROM userFollow
where userId = #{followId}
and followId = #{userId}

SELECT count(*)
FROM userFollow
WHERE userId = #{userId}
AND followId = #{followId}

mapper.insert:
     		INSERT INTO userFollow(
            userId,
            followId
					)
            VALUES (
            #{userId},
            #{followId}
            )

mapper.delete:
DELETE FROM userFollow
WHERE userId = #{userId}
and followId = #{followId}
